<div class="row inbox">
        <div class="inbox-header">
            <h1 class="pull-left">Написать сообщение</h1>
        </div>
        <div class="inbox-loading" style="display: none;">
            Loading...
        </div>
        <div class="inbox-content"><form class="inbox-compose form-horizontal" id="fileupload" action="#" method="POST" enctype="multipart/form-data">

                <div class="inbox-form-group">
                    <label class="control-label">Тема:</label>
                    <div class="controls">
                        <input type="text" class="form-control" name="subject" >
                    </div>
                </div>
                <div class="inbox-form-group">
                    <div class="controls-row">
                        <textarea class="inbox-editor inbox-wysihtml5 form-control" name="message" rows="12"></textarea>
                    </div>
                </div>

                <div class="inbox-compose-btn">
                    <button class="btn blue"><i class="fa fa-check"></i>Отправить</button>
                </div>
            </form></div>
</div>