(5275, 113, 'Седло выездковое"Prince",для пони арт.505160P', 10944, NULL, '<div style="color: rgb(51, 0, 0); font-family: Arial, Helvetica, sans-serif;">\n  - из воловьей кожи \n</div> \n<div style="color: rgb(51, 0, 0); font-family: Arial, Helvetica, sans-serif;">\n  - прочные швы \n</div> \n<div style="color: rgb(51, 0, 0); font-family: Arial, Helvetica, sans-serif;">\n  - длинные приструги \n</div> \n<div style="color: rgb(51, 0, 0); font-family: Arial, Helvetica, sans-serif;">\n  - из прочной, черной воловьей кожи&nbsp; \n</div> \n<div style="color: rgb(51, 0, 0); font-family: Arial, Helvetica, sans-serif;">\n  - сиденье, подушка седла и коленные упоры из синтетики \n</div> \n<div style="color: rgb(51, 0, 0); font-family: Arial, Helvetica, sans-serif;">\n  - хороший покрой&nbsp; \n</div> \n<div style="color: rgb(51, 0, 0); font-family: Arial, Helvetica, sans-serif;">\n  - удобное,глубокое сиденье \n</div> \n<div style="color: rgb(51, 0, 0); font-family: Arial, Helvetica, sans-serif;">\n  - размер 16&quot; / 40 см \n</div>', NULL, NULL, 1, 5020),
(5276, 113, 'Седло для Mini-Shetty арт.50520', 4160, NULL, '<p> &nbsp;</p> \n<div>\n  - из синтетического материала&nbsp; \n</div> \n<div>\n  - с ручкой&nbsp; \n</div> \n<div>\n  - новая модель&nbsp; \n</div> \n<div>\n  - очень крепкая и долговечная \n</div> \n<div>\n  - подобный замше материал дает хорошее сцепление \n</div> \n<div>\n  - с усиленными нейлоном путлища \n</div> \n<div>\n  - с кольцом для ремня хвоста&nbsp; \n</div> \n<div>\n  - размер сиденья 10 \n</div> \n<div>\n  &nbsp; \n</div>', NULL, NULL, 1, 5021),
(5277, 113, 'Седло для Mini-Shetty арт.50522', 5312, NULL, '<p> - для шетти и мини шетти</p> \n<p> - из синтетической кожи</p> \n<p> - с мягким сиденьем</p> \n<p> - передний и задний разрез выше</p> \n<p> - ремень &nbsp;нейлоновый, армированный</p> \n<p> - длинные ремни обхвата</p> \n<p> - размер 25,4 см / 10</p> \n<p> - 30,5 см / 12</p>', NULL, NULL, 1, 5022),
(5278, 113, 'Седло для Pony и Shetty,Wintec арт. 2121', 8256, NULL, '<p> &nbsp;</p> \n<div>\n  - для начинающих \n</div> \n<div>\n  - очень мягкое \n</div> \n<div>\n  - удобное, глубокие сиденье&nbsp; \n</div> \n<div>\n  - длинные ремни&nbsp; \n</div> \n<div>\n  - гарантирантия надежного закрепления&nbsp; \n</div> \n<div>\n  - регулируемая застежка-липучка&nbsp; \n</div> \n<div>\n  - сидение 29 см \n</div> \n<div>\n  - длина изделия 37 см \n</div> \n<div>\n  - из высокотехнологичного материала,под кожу \n</div> \n<div>\n  - для ребенка,некрупного подростка \n</div>', NULL, NULL, 1, 5023),
(5279, 113, 'Седло для пони ''Loesdaus Flexible''арт.50514', 6080, NULL, '<p> &nbsp;</p> \n<div>\n  - из синтетической кожи \n</div> \n<div>\n  - мягкое сиденье \n</div> \n<div>\n  - спереди и сзади скроено выше \n</div> \n<div>\n  - хорошая опора \n</div> \n<div>\n  - пристяжные ремни на седле \n</div> \n<div>\n  - ширина камеры регулируется \n</div> \n<div>\n  - ремень укреплен нейлоном \n</div> \n<div>\n  - кольца для ремня на хвосте лошади&nbsp; \n</div> \n<div>\n  - размер 36 см / 14 \n</div>', NULL, NULL, 1, 5024),
(5280, 113, 'Седло универсальное для пони,Wintec арт.10921', 30848, NULL, '<div>\n  - комфортабельное&nbsp; \n</div> \n<div>\n  - легкое - 4 кг&nbsp; \n</div> \n<div>\n  - заботится о превосходном комфорте для всадников и лошади \n</div> \n<div>\n  - со сменным ленчиком \n</div> \n<div>\n  - с длинными пристругами \n</div> \n<div>\n  &nbsp; \n</div>', NULL, NULL, 1, 5025),
(5281, 113, 'Универсальное седло ''Loesdaus Atlanta I Pony''арт.5006P', 14080, NULL, '<p> &nbsp;</p> \n<div>\n  - удобная форма для лошади и всадника \n</div> \n<div>\n  - качественная кожа \n</div> \n<div>\n  - кольца переметных сумок \n</div> \n<div>\n  - в области колена мягкая отделка \n</div> \n<div>\n  - 3 года гарантии на ленчик \n</div> \n<div>\n  - средняя ширина \n</div>', NULL, NULL, 1, 5026),
(5282, 113, 'Универсальное седло для пони ''John Webb'' арт.50100', 42880, NULL, '<p> &nbsp;</p> \n<div>\n  - прочное \n</div> \n<div>\n  - удобное \n</div> \n<div>\n  - из качественной натуральной кожи \n</div> \n<div>\n  - удобная посадка \n</div> \n<div>\n  - мягкое сиденье \n</div> \n<div>\n  - длинные коленные упоры \n</div> \n<div>\n  - хорошее сцепление с седлом \n</div> \n<div>\n  - внутренняя подкладка защищает спину лошади \n</div> \n<div>\n  - ламинированное дерево \n</div> \n<div>\n  - с каркасом из стали \n</div> \n<div>\n  - размеры&nbsp; \n</div> \n<div>\n  - 1 -для небольшого и среднего объема \n</div> \n<div>\n  - 2- на средний объем&nbsp; \n</div> \n<div>\n  - 3- экстра-широкий объем \n</div>', NULL, NULL, 1, 5027);
